import React, { useState, useEffect } from "react";
import RequestItem from "./RequestItem.jsx";

const RequestList = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    // Suponha que você carregue as requisições de um servidor aqui
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const response = await fetch("http://localhost:8081/requests");
      const result = await response.json();
      if (response.ok) {
        setRequests(result.requests);
      } else {
        console.error(result.message);
      }
    } catch (error) {
      console.error("Erro ao carregar as requisições:", error);
    }
  };

  const handleUpdateRequest = (requisicao_id, musica_id, status) => {
    // Atualiza a lista de requisições após uma ação
    setRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.requisicao_id === requisicao_id
          ? {
              ...request,
              musicas: request.musicas.map((musica) =>
                musica.id === musica_id ? { ...musica, status_text: status } : musica
              ),
            }
          : request
      )
    );
  };

  return (
    <div className="request-list">
      <h2>Lista de Requisições</h2>
      {requests.length > 0 ? (
        requests.map((request) => (
          <RequestItem
            key={request.requisicao_id}
            request={request}
            onActionComplete={handleUpdateRequest}
          />
        ))
      ) : (
        <p>Sem requisições no momento.</p>
      )}
    </div>
  );
};

export default RequestList;
