import React, { useEffect, useState } from "react";
import axios from "axios";
import RequestItem from "./RequestItem";

const RequestList = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await axios.get("http://localhost:8081/get-requests", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Adiciona token JWT ao cabeçalho
          },
        });
        setRequests(response.data); // Define os dados recebidos como estado
      } catch (err) {
        console.error("Erro ao buscar requisições:", err);
        setError("Não foi possível carregar as requisições.");
      } finally {
        setLoading(false);
      }
    };

    fetchRequests();
  }, []);

  if (loading) {
    return <div>Carregando...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="request-list">
      {requests.map((request, index) => (
        <RequestItem key={index} request={request} />
      ))}
      <div className="view-more">Ver Mais </div>
    </div>
  );
};

export default RequestList;
