import { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import astroThunder from "../assets/astroThunder.jpeg";
import 'boxicons/css/boxicons.min.css';
import '../styles/HomeRequest.css';

export default function App() {
  const [username, setUsername] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [songs, setSongs] = useState([]); // State for storing search results
  const location = useLocation();
  const navigate = useNavigate();
  const storedToken = localStorage.getItem('token');

  useEffect(() => {
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem('token');
          navigate('/');
        } else {
          setUsername(decodedToken.username);
        }
      } catch (error) {
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem('token');
        navigate('/');
      }
    } else {
      navigate('/');
    }
  }, [storedToken, navigate]);

  // Function to handle search submission
  const handleSearch = async () => {
    try {
      const response = await axios.post('http://localhost:8081/search', { query: searchQuery });
      setSongs(response.data); // Update songs state with search results
    } catch (error) {
      console.error("Erro ao buscar músicas:", error);
    }
  };

  // Function to handle "Enter" key in the search input
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div id="app" className="home-page">
      <main>

        
          <div className="user-info">
            <p>Bem-vindo, {username}!</p>
          </div>
        )}

        <div className="search-bar">
          <div className="input-container">
            <i 
              className="bx bx-search-alt-2"
              onClick={handleSearch}  // Add onClick event to icon
              style={{ cursor: 'pointer' }}  // Optional: change cursor style
            ></i>
            <input
              type="text"
              name="searchInput"
              id="searchInput"
              className="searchInput"
              placeholder="Escolha uma música"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDown={handleKeyDown}
            />
          </div>
        </div>
        
        <div className="music-content">
          {/* Render search results */}
          {songs.map((song, index) => (
            <div className="music-cover" key={index}>
              <img src={song.album.images[0]?.url} alt="Song Cover" className="cover-image" />
              <h3>{song.name}</h3>
              <p>{song.artists.map(artist => artist.name).join(', ')}</p>
            </div>
          ))}
        </div>

        <aside className="selected-songs">
          <h3>Músicas escolhidas</h3>
          {/* Display selected songs (if needed) */}
          <div className="selected-song-item">
            <img src={astroThunder} alt="Song" />
            <h4>ASTROTHUNDER</h4>
            <p>Travis Scott</p>
          </div>
          <div id="request-button">
            <button>Requisitar</button>
          </div>
        </aside>
      </main>
    </div>
  );
}
