import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { io } from "socket.io-client";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import SearchBar from "./SearchBar.jsx";
import RequestList from "./RequestList.jsx";
import Footer from "./Footer.jsx";
import "../../styles/RequestManager.css";

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:8081";

function RequestManager() {
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();
  const requestListRef = useRef(null); // Ref para o componente RequestList

  useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem("token");

      if (token) {
        try {
          const decodedToken = jwtDecode(token);
          const currentTime = Date.now() / 1000;

          if (decodedToken.exp < currentTime) {
            localStorage.removeItem("token");
            navigate("/login-venue");
            toast.error("Sessão expirada. Faça login novamente.");
          } else {
            setUserId(decodedToken.id);
          }
        } catch (error) {
          console.error("Erro ao decodificar o token:", error);
          localStorage.removeItem("token");
          navigate("/login-venue");
        }
      } else {
        navigate("/login-venue");
      }
    };

    checkToken();
  }, [navigate]);

  useEffect(() => {
    let isMounted = true; // Marca o componente como montado
  
    if (userId) {
      const socket = io(SOCKET_URL);
  
      socket.emit("join-room", `venue-${userId}`);
  
      socket.on(`venue-${userId}`, (newRequest) => {
        if (isMounted) {
          console.log("Nova requisição recebida:", newRequest);
  
          if (requestListRef.current?.handleUpdateRequest) {
            requestListRef.current.handleUpdateRequest(newRequest);
          }
  
          toast.info(`Nova solicitação: ${newRequest.clienteNome}`, {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
  
      return () => {
        isMounted = false; // Marca como desmontado
        socket.off(`venue-${userId}`);
        socket.disconnect();
      };
    }
  }, [userId]);
  
  return (
    <div className="Requests">
      <div className="search-bar-container">
        <SearchBar userId={userId} />
        <button
          className="playlist-icon"
          onClick={() => navigate("/playlist")}
        >
          <img
            src={require("../../assets/playlist.png")}
            alt="Playlist Icon"
            style={{ width: "24px", height: "24px" }}
          />
        </button>
      </div>
      <RequestList ref={requestListRef} userId={userId} />
      <Footer />
      <ToastContainer />
    </div>
  );
}

export default RequestManager;
