import "bootstrap-icons/font/bootstrap-icons.css";
import logo from "../assets/logo.png";
import { useState } from "react";
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(''); // Estado para exibir a mensagem de feedback
  const navigate = useNavigate();
  
  function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:8081/login', { email, password })
      .then(res => {
        navigate('/home');
      })
      .catch(err => {
        if (err.response && err.response.status === 401) {
          setMessage("Email ou senha incorretos"); // Define mensagem de erro se as credenciais estiverem incorretas
        } else {
          setMessage("Erro ao tentar fazer login. Tente novamente."); // Mensagem de erro genérica para outros problemas
        }
      });
  }

  return (
    <div className="container">
      <img src={logo} alt="Play 4 Me Logo" />
      <h2>Welcome Back</h2>
      <p>Please login with registered account</p>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <i className="bi bi-envelope"></i>
          <input
            type="email"
            placeholder="Email"
            required
            onChange={e => setEmail(e.target.value)}
          />
        </div>
        <div className="input-group">
          <i className="bi bi-lock"></i>
          <input
            type="password" // Corrigido para o tipo "password" para ocultar a senha
            placeholder="Password"
            required
            onChange={e => setPassword(e.target.value)}
          />
        </div>

        <button type="submit" className="btn">
          Login
        </button>

        {message && <p>{message}</p>} {/* Exibe a mensagem de feedback */}

        <p>Or using other method</p>

        <button type="button" className="btn btn-alt social-btns">
          <i className="bi bi-google"></i> Sign Up with Google
        </button>

        <button type="button" className="btn btn-alt social-btns">
          <i className="bi bi-instagram"></i> Sign Up with Instagram
        </button>

        <p>
          Don&apos;t have an account? <Link to="/regist">Sign up</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
