import "bootstrap-icons/font/bootstrap-icons.css";
import logo from "../../assets/logo.png";
import { useState, useEffect } from "react";
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // Limpa a última sessão ao carregar a página de login
  useEffect(() => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
  }, []);

  function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:8081/login', { email, password })
      .then(res => {
        const { token, username, id } = res.data; // Verifique se `id` está aqui
        console.log("Dados recebidos no login:", { token, username, id }); // Log para debug
        localStorage.setItem('token', token);
        localStorage.setItem('userId', id); // Salva o id do usuário no localStorage
        navigate('/home', { state: { username, id } });
      })
      .catch(err => {
        if (err.response && err.response.status === 401) {
          setMessage("Email ou senha incorretos");
        } else {
          setMessage("Erro ao tentar fazer login. Tente novamente.");
        }
      });

  }

  return (
    <div className="login-container">
      <img src={logo} alt="Play 4 Me Logo" />
      <h2>Welcome Back</h2>
      <p>Please login with registered account</p>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <i className="bi bi-envelope"></i>
          <input
            type="email"
            placeholder="Email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-group">
          <i className="bi bi-lock"></i>
          <input
            type="password"
            placeholder="Password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn">
          Login
        </button>
        {message && <p className="message">{message}</p>}
        <p>Or using other method</p>
        <button type="button" className="btn btn-alt social-btns">
          <i className="bi bi-google"></i> Sign Up with Google
        </button>
        <button type="button" className="btn btn-alt social-btns">
          <i className="bi bi-instagram"></i> Sign Up with Instagram
        </button>
        <p>
          Don&apos;t have an account? <Link to="/regist">Sign up</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
