import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import axios from "axios";
import astroThunder from "../../assets/astroThunder.jpeg";
import "boxicons/css/boxicons.min.css";
import "../../styles/HomeRequest.css";

export default function App() {
  const [username, setUsername] = useState("");
  const [userId, setUserId] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [selected, setSelected] = useState([]);
  const [serverResponse, setServerResponse] = useState(null);
  const navigate = useNavigate();
  const storedToken = localStorage.getItem("token");
  const [isSearching, setIsSearching] = useState(false);

  const [isVisible, setIsVisible] = useState(false);
  const [startY, setStartY] = useState(0);
  const [moveY, setMoveY] = useState(0);

  const handleTouchStart = (e) => {
    setStartY(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    const currentY = e.touches[0].clientY;
    setMoveY(currentY - startY); // Calcula a diferença
  };

  const handleTouchEnd = () => {
    if (moveY < -50) {
      // Arrastou para cima
      setIsVisible(true);
    } else if (moveY > 50) {
      // Arrastou para baixo
      setIsVisible(false);
    }
    setMoveY(0);

    useEffect(() => {
      if (storedToken) {
        try {
          const decodedToken = jwtDecode(storedToken);
          const currentTime = Date.now() / 1000;

          if (decodedToken.exp < currentTime) {
            localStorage.removeItem("token");
            navigate("/login");
          } else {
            setUsername(decodedToken.username);
            setUserId(decodedToken.userId); // Extraindo e armazenando o userId do token
          }
        } catch (error) {
          console.error("Erro ao decodificar o token:", error);
          localStorage.removeItem("token");
          navigate("/login");
        }
      } else {
        navigate("/login");
      }
    }, [storedToken, navigate]);

    const handleSearch = async () => {
      setIsSearching(true);
      try {
        const response = await axios.post("http://localhost:8081/search", {
          query: searchQuery,
        });
        setSongs(response.data);
        setServerResponse(null);
      } catch (error) {
        console.error("Erro ao buscar músicas:", error);
      } finally {
        setIsSearching(false);
      }
    };

    const handleKeyDown = (e) => {
      if (e.key === "Enter") {
        handleSearch();
      }
    };

    const handleSelectSong = (song) => {
      if (!selected.some((s) => s.id === song.id)) {
        setSelected([...selected, song]);
        setServerResponse(null);
      }
    };

    const handleRequest = async () => {
      try {
        const token = localStorage.getItem("token");
        const venueId = 1; // Valor fixo, caso não seja dinâmico ainda
        console.log(userId);
        const response = await axios.post(
          "http://localhost:8081/request",
          {
            cliente_id: userId, // Utiliza o estado atualizado do userId
            venue_id: venueId,
            musicas: selected,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("Músicas enviadas com sucesso!");
        setServerResponse(response.data);
        setSelected([]);
      } catch (error) {
        console.error("Erro ao enviar músicas:", error);
      }
    };

    return (
      <main>
        <div className="main-content">
          <div className="search-bar-container">
            <div className="search-wrapper">
              <i
                className="bx bx-search-alt-2 search-icon"
                onClick={handleSearch}
                style={{ cursor: "pointer" }}
              ></i>
              <input
                type="text"
                name="searchInput"
                id="searchInput"
                className="searchInput"
                placeholder="Pesquise uma música"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyDown={handleKeyDown}
              />
            </div>
          </div>
          <div className="music-content">
            {isSearching ? (
              <span>Pesquisando...</span>
            ) : songs.length > 0 ? (
              songs.map((song, index) => (
                <div
                  className="music-cover"
                  key={index}
                  onClick={() => handleSelectSong(song)}
                >
                  <img
                    src={song.album.images[0]?.url}
                    alt="Song Cover"
                    className="cover-image"
                  />
                  <h3>{song.name}</h3>
                  <p>{song.artists.map((artist) => artist.name).join(", ")}</p>
                </div>
              ))
            ) : (
              <span>Pesquise por uma música...</span>
            )}
          </div>
        </div>

        <aside
          className={`selected-songs ${isVisible ? "visible" : ""}`}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          <h3>
            {serverResponse ? "Resposta do Servidor" : "Músicas escolhidas"}
          </h3>
          {serverResponse ? (
            <div className="server-response">
              <p>
                {serverResponse.message || "Requisição processada com sucesso!"}
              </p>
            </div>
          ) : (
            selected.map((song, index) => (
              <div className="selected-song-item" key={index}>
                <img
                  src={song.album.images[0]?.url || astroThunder}
                  alt="Song"
                  className="selectedSongImg"
                />
                <h4>{song.name}</h4>
                <p>{song.artists.map((artist) => artist.name).join(", ")}</p>
              </div>
            ))
          )}
          <div id="request-button">
            <button onClick={handleRequest}>Requisitar</button>
          </div>
        </aside>
      </main>
    );
  };
}
