import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode"; // Certifique-se de instalar este pacote
import SearchBar from "./SearchBar.jsx";
import RequestList from "./RequestList.jsx";
import Footer from "./Footer.jsx";
import { FaMusic } from "react-icons/fa"; // Ícone de música
import "../../RequestManager.css"; // Certifique-se de criar o CSS

function App() {
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000;

        // Verifica se o token expirou
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem("token");
          navigate("/login-venue");
        } else {
          setUserId(decodedToken.id); // Define o ID do usuário do token
        }
      } catch (error) {
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem("token");
        navigate("/login-venue");
      }
    } else {
      navigate("/login-venue"); // Redireciona para login se não houver token
    }
  }, [navigate]);

  // Função para navegar para a página de playlist
  const goToPlaylist = () => {
    navigate("/playlist");
  };

  return (
    <div className="Requests">
      <div className="search-bar-container">
        <SearchBar userId={userId} />
        <button className="playlist-icon" onClick={goToPlaylist}>
          <FaMusic size={24} />
        </button>
      </div>
      <RequestList userId={userId} />
      <Footer />
    </div>
  );
}

export default App;
