import { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'; // Corrigindo o uso do useLocation
import { jwtDecode } from 'jwt-decode';
import astroThunder from "../assets/astroThunder.jpeg";
import 'boxicons/css/boxicons.min.css';
import '../styles/HomeRequest.css';

export default function App() {
  const [username, setUsername] = useState('');  // Criando o estado para o username
  const location = useLocation();
  const navigate = useNavigate();
  const storedToken = localStorage.getItem('token');

  useEffect(() => {
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;  // Tempo atual em segundos
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem('token');
          navigate('/');
        } else {
          setUsername(decodedToken.username);  // Atualiza o estado 'username'
        }
      } catch (error) {
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem('token');
        navigate('/');
      }
    } else {
      navigate('/login');
    }
  }, [storedToken, navigate]); // O useEffect será executado toda vez que 'storedToken' ou 'navigate' mudar.

  return (
    <div id="app" className="home-page">
      <main>

        {username && (
          <div className="user-info">
            <p>Bem-vindo, {username}!</p>
          </div>
        )}
        
        <div className="search-bar">
          <div className="input-container">
            <i className="bx bx-search-alt-2"></i>
            <input
              type="text"
              name="searchInput"
              id="searchInput"
              className="searchInput"
              placeholder="Escolha uma música"
            />
          </div>
        </div>
        <div className="music-content">
          {/* Repetindo a estrutura de músicas */}
          {[...Array(8)].map((_, index) => (
            <div className="music-cover" key={index}>
              <img src={astroThunder} alt="Song Cover" className="cover-image" />
              <h3>ASTROTHUNDER</h3>
              <p>Travis Scott</p>
            </div>
          ))}
        </div>
        <aside className="selected-songs">
          <h3>Músicas escolhidas</h3>
          <div className="selected-song-item">
            <img src={astroThunder} alt="Song" />
            <h4>ASTROTHUNDER</h4>
            <p>Travis Scott</p>
          </div>
          <div id="request-button">
            <button>Requisitar</button>
          </div>
        </aside>
      </main>
    </div>
  );
}
