import { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import astroThunder from "../../assets/astroThunder.jpeg";
import 'boxicons/css/boxicons.min.css';
import '../../styles/HomeRequest.css';

export default function App() {
  const [username, setUsername] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [songs, setSongs] = useState([]); // State for storing search results
  const [selected, setSelected] = useState([]); // Array for selected songs
  const [serverResponse, setServerResponse] = useState(null); // State for server response
  const location = useLocation();
  const navigate = useNavigate();
  const storedToken = localStorage.getItem('token');

  useEffect(() => {
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem('token');
          navigate('/login');
        } else {
          setUsername(decodedToken.username);
        }
      } catch (error) {
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem('token');
        navigate('/login');
      }
    } else {
      navigate('/login');
    }
  }, [storedToken, navigate]);

  // Function to handle search submission
  const handleSearch = async () => {
    try {
      const response = await axios.post('http://localhost:8081/search', { query: searchQuery });
      setSongs(response.data); // Update songs state with search results
      setServerResponse(null); // Clear server response when new search is made
    } catch (error) {
      console.error("Erro ao buscar músicas:", error);
    }
  };

  // Function to handle "Enter" key in the search input
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  // Function to handle song selection
  const handleSelectSong = (song) => {
    if (!selected.some(s => s.id === song.id)) { // Prevent duplicate selection
      setSelected([...selected, song]); // Add selected song to the array
      setServerResponse(null); // Clear server response when new songs are selected
    }
  };

  // Function to handle request button click
  const handleRequest = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error("Token de autenticação não encontrado.");
        return;
      }

      const response = await axios.post(
        'http://localhost:8081/request',
        { songs: selected },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      console.log("Músicas enviadas com sucesso!");
      setServerResponse(response.data); // Set server response
      setSelected([]); // Clear selected songs
    } catch (error) {
      console.error("Erro ao enviar músicas:", error);
    }
  };

  return (
    <div id="app" className="home-page">
      <main>

        <div className="search-bar">
          <div className="input-container">
            <i
              className="bx bx-search-alt-2"
              onClick={handleSearch}
              style={{ cursor: 'pointer' }}
            ></i>
            <input
              type="text"
              name="searchInput"
              id="searchInput"
              className="searchInput"
              placeholder="Escolha uma música"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDown={handleKeyDown}
            />
          </div>
        </div>

        <div className="music-content">
          {songs.map((song, index) => (
            <div className="music-cover" key={index} onClick={() => handleSelectSong(song)}>
              <img src={song.album.images[0]?.url} alt="Song Cover" className="cover-image" />
              <h3>{song.name}</h3>
              <p>{song.artists.map(artist => artist.name).join(', ')}</p>
            </div>
          ))}
        </div>

        <aside className="selected-songs">
          <h3>{serverResponse ? 'Resposta do Servidor' : 'Músicas escolhidas'}</h3>
          {serverResponse ? (
            <div className="server-response">
              <p>{serverResponse.message || "Requisição processada com sucesso!"}</p>
              {/* Render additional response details if necessary */}
            </div>
          ) : (
            selected.map((song, index) => (
              <div className="selected-song-item" key={index}>
                <img src={song.album.images[0]?.url || astroThunder} alt="Song" />
                <h4>{song.name}</h4>
                <p>{song.artists.map(artist => artist.name).join(', ')}</p>
              </div>
            ))
          )}
          <div id="request-button">
            <button onClick={handleRequest}>Requisitar</button>
          </div>
        </aside>
      </main>
    </div>
  );
}
