import React, { useEffect, useState } from "react";
import axios from "axios";
import RequestItem from "./RequestItem";

const RequestList = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await axios.get("http://localhost:8081/getrequests", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Adiciona token JWT ao cabeçalho
          },
        });
        setRequests(response.data); // Define os dados recebidos como estado
      } catch (err) {
        console.error("Erro ao buscar requisições:", err);
        setError("Não foi possível carregar as requisições.");
      } finally {
        setLoading(false);
      }
    };

    fetchRequests();
  }, []);

  const handleActionComplete = (requisicaoId, musicaId, statusText) => {
    // Atualiza o estado para refletir a ação de aprovação ou negação
    setRequests((prevRequests) =>
      prevRequests.map((request) => {
        if (request.requisicao_id === requisicaoId) {
          return {
            ...request,
            musicas: request.musicas.map((musica) =>
              musica.id === musicaId
                ? { ...musica, status_text: statusText }
                : musica
            ),
          };
        }
        return request;
      })
    );
  };

  if (loading) {
    return <div>Carregando...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="request-list">
      {requests.map((request) => (
        <RequestItem
          key={request.requisicao_id}
          request={request}
          onActionComplete={handleActionComplete} // Passa o callback
        />
      ))}
    </div>
  );
};

export default RequestList;
