import React from "react";
import PropTypes from "prop-types";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../../styles/Playlist.css"; // Estilo CSS personalizado

const Playlist = ({ playlist }) => {
  const handleAction = async (actionType) => {
    try {
      // Simulação de uma requisição
      const response = await fetch("http://localhost:8081/playlist-action", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ actionType }),
      });

      if (response.ok) {
        toast.success("Ação realizada com sucesso!");
      } else {
        const result = await response.json();
        toast.error(result.message || "Algo deu errado. Tente novamente.");
      }
    } catch (error) {
      console.error("Erro ao executar a ação:", error);
      toast.error("Erro ao processar a ação. Verifique sua conexão.");
    }
  };

  return (
    <div className="playlist-container">
      <ToastContainer /> {/* Container de notificações */}

      <h2>Sua Playlist</h2>

      {playlist && playlist.length > 0 ? (
        playlist.map((musica) => (
          <div key={musica.id} className="music-item">
            <img
              src={musica.imagem || "https://via.placeholder.com/50"}
              alt={musica.nome}
              className="music-thumbnail"
            />
            <div className="music-details">
              <p className="music-title">{musica.nome}</p>
              <p>Duração: {Math.floor(musica.duracao / 60)}m {musica.duracao % 60}s</p>
            </div>
            <div className="music-actions">
              <button
                className="approve"
                onClick={() => handleAction("approve")}
              >
                Aprovar
              </button>
              <button
                className="deny"
                onClick={() => handleAction("deny")}
              >
                Rejeitar
              </button>
            </div>
          </div>
        ))
      ) : (
        <p className="empty-playlist">Nenhuma música na playlist.</p>
      )}
    </div>
  );
};

Playlist.propTypes = {
  playlist: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      nome: PropTypes.string.isRequired,
      imagem: PropTypes.string,
      duracao: PropTypes.number.isRequired,
    })
  ).isRequired,
};

export default Playlist;
