import React from "react";
import PropTypes from "prop-types"; // Para validação de propriedades
import music_photo from "../../assets/astroThunder.jpeg";

const RequestItem = ({ request }) => {
  return (
    <div className="request-item">
      <div className="request-avatar">
        <div className="avatar-placeholder">
          {/* Exibe as iniciais do usuário caso a imagem não exista */}
          {request.user && request.user.charAt(0).toUpperCase()}
        </div>
        <div className="user-info">
          <p>{request.user} (ID: {request.id})</p>
        </div>
      </div>

      <div className="request-details">
        <img src={music_photo} alt={request.song} />
        <div className="song-details">
          <p><strong>Canção:</strong> {request.song}</p>
          <p><strong>Hora:</strong> {request.time}</p>
          <p><strong>Preço:</strong> ${request.price}</p>
        </div>
      </div>

      <div className="request-status">
        {request.status === "approved" ? (
          <span className="approved" title="Aprovado">&#10003;</span>
        ) : (
          <span className="denied" title="Negado">&#10060;</span>
        )}
      </div>
    </div>
  );
};

RequestItem.propTypes = {
  request: PropTypes.shape({
    user: PropTypes.string.isRequired,
    id: PropTypes.number.isRequired,
    song: PropTypes.string.isRequired,
    time: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    status: PropTypes.string.isRequired,
  }).isRequired,
};

export default RequestItem;
