import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { io } from "socket.io-client";
import { ToastContainer, toast } from "react-toastify"; // Import do Toast
import "react-toastify/dist/ReactToastify.css"; // Estilos do Toast
import SearchBar from "./SearchBar.jsx";
import RequestList from "./RequestList.jsx";
import Footer from "./Footer.jsx";
import "../../styles/RequestManager.css";

// Definindo a URL do servidor Socket.IO (pode ser configurada via variável de ambiente)
const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:8081";

function App() {
  const [userId, setUserId] = useState(null);
  const [requests, setRequests] = useState([]); // Estado para armazenar requests
  const navigate = useNavigate();

  useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem("token");

      if (token) {
        try {
          const decodedToken = jwtDecode(token);
          const currentTime = Date.now() / 1000;

          if (decodedToken.exp < currentTime) {
            localStorage.removeItem("token");
            navigate("/login-venue");
            toast.error("Sessão expirada. Faça login novamente.");
          } else {
            setUserId(decodedToken.id);
          }
        } catch (error) {
          console.error("Erro ao decodificar o token:", error);
          localStorage.removeItem("token");
          navigate("/login-venue");
        }
      } else {
        navigate("/login-venue");
      }
    };

    checkToken();
  }, [navigate]);

  useEffect(() => {
    if (userId) {
      const socket = io("http://localhost:8081");
socket.emit("join-room", `venue-${userId}`);

      // Escutar eventos do servidor para o venue específico
      socket.on(`venue-${userId}`, (data) => {
        console.log("Nova requisição recebida:", data);

        // Adicionar nova request ao estado
        setRequests((prevRequests) => [data, ...prevRequests]);

        // Exibir o Toast
        toast.info(`Nova solicitação: ${data.songName} de ${data.userName}`, {
          position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
      });

      // Limpar listener ao desmontar componente
      return () => {
        socket.off(`venue-${userId}`);
      };
    }
  }, [userId]);

  return (
    <div className="Requests">
      <div className="search-bar-container">
        <SearchBar userId={userId} />
        <button
          className="playlist-icon"
          onClick={() => navigate("/playlist")}
        >
          <img
            src={require("../../assets/playlist.png")}
            alt="Playlist Icon"
            style={{ width: "24px", height: "24px" }}
          />
        </button>
      </div>
      {/* Passar a lista de requests atualizada para o componente RequestList */}
      <RequestList userId={userId} requests={requests} />
      <Footer />
      <ToastContainer /> {/* Componente para exibir os Toasts */}
    </div>
  );
}

export default App;
