import React, { useEffect, useState } from "react";
import axios from "axios";
import RequestItem from "./RequestItem";
import { io } from "socket.io-client";

// Definindo a URL do servidor Socket.IO (pode ser configurada via variável de ambiente)
const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:8081";

const RequestList = ({ userId }) => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Inicializa a conexão do socket
    const socket = io(SOCKET_URL);

    // Função para buscar as requisições
    const fetchRequests = async () => {
      try {
        const response = await axios.get("http://localhost:8081/getrequests", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Adiciona token JWT ao cabeçalho
          },
        });
        setRequests(response.data); // Define os dados recebidos como estado
      } catch (err) {
        console.error("Erro ao buscar requisições:", err);
        setError("Não foi possível carregar as requisições.");
      } finally {
        setLoading(false);
      }
    };

    // Buscar as requisições assim que o componente for montado
    fetchRequests();

    // Escutar por novas requisições via WebSocket
    if (userId) {
      console.log(userId)
      socket.on(`venue-${userId}`, (newRequest) => {
        console.log("Nova requisição recebida:", newRequest);
        console.log(userId)
        // Adicionar nova requisição ao estado
        setRequests((prevRequests) => [newRequest, ...prevRequests]);
      });

      // Limpar listener ao desmontar ou quando userId mudar
      return () => {
        socket.off(`venue-${userId}`);
      };
    }
  }, [userId]); // Recarrega quando userId mudar

  // Função para atualizar uma requisição após a ação de aprovar/negara música
  const handleUpdateRequest = (updatedRequest) => {
    setRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.requisicao_id === updatedRequest.requisicao_id
          ? updatedRequest
          : request
      )
    );
  };

  if (loading) {
    return <div>Carregando...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="request-list">
      {requests
        .map((request) => {
          // Filtra as músicas com status "PENDING"
          const pendingMusicas = request.musicas.filter(
            (musica) => musica.status_text === "PENDING"
          );

          if (pendingMusicas.length === 0) {
            return null; // Não renderiza requisições sem músicas com status "PENDING"
          }

          return (
            <RequestItem
              key={request.requisicao_id}
              request={{ ...request, musicas: pendingMusicas }}
              onUpdateRequest={handleUpdateRequest} // Passa a função para atualizar a requisição
            />
          );
        })
        .filter(Boolean)} {/* Remove as requisições que não têm músicas com status "PENDING" */}
    </div>
  );
};

export default RequestList;
