import React, { useEffect, useState } from "react";
import axios from "axios";
import "../../styles/Playlist.css";

const Playlist = ({ token }) => {
  const [playlist, setPlaylist] = useState([]);
  const [error, setError] = useState(null);

  // Função para buscar as músicas do servidor
  const fetchPlaylist = async () => {
    try {
      const response = await axios.get("/getrequests", {
        headers: {
          Authorization: `Bearer ${token}`, // Envia o token no cabeçalho
        },
      });
      setPlaylist(response.data);
    } catch (err) {
      console.error("Erro ao buscar a playlist:", err);
      setError("Não foi possível carregar a playlist.");
    }
  };

  // Chama a API quando o componente é montado
  useEffect(() => {
    fetchPlaylist();
  }, []);

  return (
    <div className="playlist-container">
      <h2>Playlist de Músicas Aceitas</h2>
      {error ? (
        <p className="error-message">{error}</p>
      ) : playlist.length > 0 ? (
        playlist.map((request) =>
          request.musicas.map((musica) => (
            <div className="music-item" key={musica.id}>
              <div className="music-thumbnail">
                <img
                  src={musica.imagem || "https://via.placeholder.com/50"}
                  alt={musica.nome}
                />
              </div>
              <div className="music-details">
                <p className="music-title">{musica.nome}</p>
                <p className="music-artist">Duração: {musica.duracao}</p>
              </div>
            </div>
          ))
        )
      ) : (
        <p className="empty-playlist">Nenhuma música foi aceita ainda.</p>
      )}
    </div>
  );
};

export default Playlist;
