import { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import astroThunder from "../assets/astroThunder.jpeg";
import 'boxicons/css/boxicons.min.css';
import '../styles/HomeRequest.css';
import jwtDecode from 'jwt-decode';

export default function App() {
  const location = useLocation();
  const navigate = useNavigate();
  const storedToken = localStorage.getItem('token');
  let username = '';

  useEffect(() => {
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;

        // Verifica se o token está expirado
        if (decodedToken.exp < currentTime) {
          // Remove o token e redireciona para a página de login
          localStorage.removeItem('token');
          navigate('/login');
        } else {
          username = decodedToken.username;
        }
      } catch (error) {
        // Se houver um erro na decodificação, remova o token e redirecione para login
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem('token');
        navigate('/login');
      }
    } else {
      navigate('/login'); // Redireciona para login se o token não existir
    }
  }, [storedToken, navigate]);

  return (
    <div id="app" className="home-page">
      <main>
        {username && (
          <div className="user-info">
            <p>Bem-vindo, {username}!</p> {/* Exibe o nome do usuário */}
          </div>
        )}

        <div className="search-bar">
          <div className="input-container">
            <i className="bx bx-search-alt-2"></i>
            <input
              type="text"
              name="searchInput"
              id="searchInput"
              className="searchInput"
              placeholder="Escolha uma música"
            />
          </div>
        </div>
        
        <div className="music-content">
          <div className="music-cover">
            <img src={astroThunder} alt="Song Cover" className="cover-image" />
            <h3>ASTROTHUNDER</h3>
            <p>Travis Scott</p>
          </div>
          {/* Repetição de itens musicais omitida para brevidade */}
        </div>
        
        <aside className="selected-songs">
          <h3>Músicas escolhidas</h3>
          <div className="selected-song-item">
            <img src={astroThunder} alt="Song" />
            <h4>ASTROTHUNDER</h4>
            <p>Travis Scott</p>
          </div>
          <div id="request-button">
            <button>Requisitar</button>
          </div>
        </aside>
        
      </main>
    </div>
  );
}
