import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Modal from "./Modal.jsx";
import "../../styles/RequestItem.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const RequestItem = () => {
  const [musicas, setMusicas] = useState([]);
  const [cliente, setCliente] = useState({});
  const [dataRequisicao, setDataRequisicao] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedMusica, setSelectedMusica] = useState(null);
  const [currentAction, setCurrentAction] = useState(null);

  // Função para buscar os dados da requisição
  const fetchRequestData = async () => {
    try {
      const response = await fetch("http://localhost:8081/getrequests");
      const result = await response.json();
      setMusicas(result.musicas);
      setCliente(result.cliente);
      console.log()
      setDataRequisicao(result.data_requisicao);
    } catch (error) {
      console.error("Erro ao buscar os dados da requisição:", error);
    }
  };

  // Buscar os dados ao carregar o componente
  useEffect(() => {
    fetchRequestData();
  }, []);

  const handleOpenModal = (musica, action) => {
    setSelectedMusica(musica);
    setCurrentAction(action);
    setIsModalOpen(true);
  };

  const handleSubmitComment = async (comment) => {
    const statusText = currentAction === "approve" ? "approve" : "deny";

    try {
      const response = await fetch("http://localhost:8081/process-action", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          music_id: selectedMusica.id,
          status_text: statusText,
          comentario: comment,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        const actionMessage =
          statusText === "approve" ? "Música Aceite!" : "Música Rejeitada!";
        toast.success(actionMessage);

        // Recarregar a lista após a ação
        fetchRequestData();
      } else {
        toast.error(result.message || "Algo deu errado. Tente novamente.");
      }
    } catch (error) {
      console.error("Erro ao enviar o status:", error);
      toast.error("Erro ao processar a ação. Verifique sua conexão.");
    } finally {
      setIsModalOpen(false);
    }
  };

  // Filtra apenas as músicas com status 'PENDING'
  const musicasPending = musicas.filter(
    (musica) => musica.status_text === "PENDING"
  );

  return (
    <div className="request-item">
      <ToastContainer />

      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleSubmitComment}
        title={`${
          currentAction === "approve" ? "Aprovar" : "Negar"
        } Música`}
        placeholder="Insira seu comentário..."
      />

      <div className="request-header">
        <div className="request-avatar">
          <div className="avatar-placeholder">
            {cliente.nome?.charAt(0).toUpperCase()}
          </div>
        </div>
        <div className="user-info">
          <p>
            <strong>{cliente.nome}</strong>
          </p>
        </div>
      </div>

      <div className="music-list">
        <h4>Músicas Requisitadas:</h4>
        {musicasPending.length > 0 ? (
          musicasPending.map((musica, index) => (
            <div key={index} className="music-item">
              <img
                src={musica.imagem || "https://via.placeholder.com/50"}
                alt={musica.nome}
                className="music-thumbnail"
              />
              <div className="music-details">
                <p>
                  <strong>Nome:</strong> {musica.nome}
                </p>
                <p>
                  <strong>Duração:</strong>{" "}
                  {Math.floor(musica.duracao / 60000)}m{" "}
                  {((musica.duracao % 60000) / 1000).toFixed(0)}s
                </p>
              </div>
              <div className="music-actions">
                <button
                  className="approve"
                  onClick={() => handleOpenModal(musica, "approve")}
                >
                  ✓
                </button>
                <button
                  className="deny"
                  onClick={() => handleOpenModal(musica, "deny")}
                >
                  ✗
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Sem músicas pendentes nesta requisição.</p>
        )}
      </div>

      <div className="request-footer">
        <p>
          <strong>Data da Requisição:</strong>{" "}
          {new Date(dataRequisicao).toLocaleString()}
        </p>
      </div>
    </div>
  );
};

RequestItem.propTypes = {
  // Nenhum requestId necessário, já que estamos obtendo todos os dados
};

export default RequestItem;
