import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import axios from "axios";
import "boxicons/css/boxicons.min.css";
import "../../styles/HomeRequest.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function App() {
  const [username, setUsername] = useState("");
  const [userId, setUserId] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [selected, setSelected] = useState([]);
  const [webSocketMessages, setWebSocketMessages] = useState([]); // Novo estado para mensagens do WebSocket
  const navigate = useNavigate();
  const storedToken = localStorage.getItem("token");
  const [isSearching, setIsSearching] = useState(false);

  const [isVisible, setIsVisible] = useState(false);
  const [startY, setStartY] = useState(0);
  const [moveY, setMoveY] = useState(0);

  const handleTouchStart = (e) => {
    setStartY(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    const currentY = e.touches[0].clientY;
    setMoveY(currentY - startY);
  };

  const handleTouchEnd = () => {
    if (moveY < -50) {
      setIsVisible(true);
    } else if (moveY > 50) {
      setIsVisible(false);
    }

    setMoveY(0);
  };

  // Adicionando lógica do WebSocket
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8081"); // Conexão com o WebSocket

    ws.onopen = () => {
      console.log("Conexão WebSocket estabelecida!");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setWebSocketMessages((prevMessages) => [...prevMessages, data]);
      console.log("Mensagem recebida:", data);
    };

    ws.onclose = () => {
      console.log("Conexão WebSocket fechada.");
    };

    ws.onerror = (error) => {
      console.error("Erro no WebSocket:", error);
    };

    return () => {
      ws.close(); // Fechar a conexão quando o componente for desmontado
    };
  }, []);

  useEffect(() => {
    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;

        if (decodedToken.exp < currentTime) {
          localStorage.removeItem("token");
          navigate("/login");
        } else {
          setUsername(decodedToken.username);
          setUserId(decodedToken.userId); // Extraindo e armazenando o userId do token
        }
      } catch (error) {
        console.error("Erro ao decodificar o token:", error);
        localStorage.removeItem("token");
        navigate("/login");
      }
    } else {
      navigate("/login");
    }
  }, [storedToken, navigate]);

  const handleSearch = async () => {
    setIsSearching(true);
    try {
      const response = await axios.post("http://localhost:8081/search", {
        query: searchQuery,
      });
      setSongs(response.data);
    } catch (error) {
      console.error("Erro ao buscar músicas:", error);
      toast.error("Erro ao buscar músicas. Tente novamente.");
    } finally {
      setIsSearching(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  const handleSelectSong = (song) => {
    if (!selected.some((s) => s.id === song.id)) {
      setSelected([...selected, song]);
    }
  };

  const handleRequest = async () => {
    try {
      const token = localStorage.getItem("token");
      const venueId = 1; // Valor fixo, caso não seja dinâmico ainda

      const response = await axios.post(
        "http://localhost:8081/request",
        {
          cliente_id: userId,
          venue_id: venueId,
          musicas: selected,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSelected([]);
      toast.success(response.data.message || "Requisição enviada!");
    } catch (error) {
      console.error("Erro ao enviar músicas:", error);
      toast.error("Erro ao enviar requisição. Tente novamente.");
    }
  };

  return (
    <main>
      <ToastContainer />
      <div className="main-content">
        <div className="search-bar-container">
          <div className="search-wrapper">
            <i
              className="bx bx-search-alt-2 search-icon"
              onClick={handleSearch}
              style={{ cursor: "pointer" }}
            ></i>
            <input
              type="text"
              name="searchInput"
              id="searchInput"
              className="searchInput"
              placeholder="Pesquise uma música"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDown={handleKeyDown}
            />
          </div>
        </div>
        <div className="music-content">
          {isSearching ? (
            <span>Pesquisando...</span>
          ) : songs.length > 0 ? (
            songs.map((song, index) => (
              <div
                className="music-cover"
                key={index}
                onClick={() => handleSelectSong(song)}
              >
                <img
                  src={song.album.images[0]?.url}
                  alt="Song Cover"
                  className="cover-image"
                />
                <h3>{song.name}</h3>
                <p>{song.artists.map((artist) => artist.name).join(", ")}</p>
              </div>
            ))
          ) : (
            <span>Pesquise por uma música...</span>
          )}
        </div>
        <div>
          <h3>Mensagens do WebSocket</h3>
          <ul>
            {webSocketMessages.map((message, index) => (
              <li key={index}>{JSON.stringify(message)}</li>
            ))}
          </ul>
        </div>
      </div>

      <aside
        className={`selected-songs ${isVisible ? "visible" : ""}`}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <h3>Músicas escolhidas</h3>
        {selected.map((song, index) => (
          <div className="selected-song-item" key={index}>
            <img
              src={song.album.images[0]?.url}
              alt="Song"
              className="selectedSongImg"
            />
            <h4>{song.name}</h4>
            <p>{song.artists.map((artist) => artist.name).join(", ")}</p>
          </div>
        ))}
        <div id="request-button">
          <button onClick={handleRequest}>Requisitar</button>
        </div>
      </aside>
    </main>
  );
}
