import React from "react";
import PropTypes from "prop-types";
import "../../styles/RequestItem.css";

const RequestItem = ({ request }) => {
  const { cliente, musicas, data_requisicao } = request;

  const handleApprove = async () =>{

  }

  const handleDeny = async () =>{
    
  }

  return (
    <div className="request-item">
      {/* Cabeçalho da requisição */}
      <div className="request-header">
        <div className="request-avatar">
          <div className="avatar-placeholder">
            {cliente.nome.charAt(0).toUpperCase()}
          </div>
        </div>
        <div className="user-info">
          <p><strong>{cliente.nome}</strong></p>
        </div>
      </div>

      {/* Lista de músicas requisitadas */}
      <div className="music-list">
        <h4>Músicas Requisitadas:</h4>
        {musicas.length > 0 ? (
          musicas.map((musica, index) => (
            <div key={index} className="music-item">
              <img
                src={musica.imagem || "https://via.placeholder.com/50"}
                alt={musica.nome}
                className="music-thumbnail"
              />
              <div className="music-details">
                <p><strong>Nome:</strong> {musica.nome}</p>
                <p><strong>Duração:</strong> {Math.floor(musica.duracao / 60000)}m {((musica.duracao % 60000) / 1000).toFixed(0)}s</p>
              </div>
              <div className="music-actions">
                <button className="approve" onClick={() => handleApprove(musica)}>
                  ✓
                </button>
                <button className="deny" onClick={() => handleDeny(musica)}>
                  ✗
                </button>
              </div>

            </div>
          ))
        ) : (
          <p>Sem músicas nesta requisição.</p>
        )}
      </div>

      {/* Rodapé da requisição */}
      <div className="request-footer">
        <p><strong>Data da Requisição:</strong> {new Date(data_requisicao).toLocaleString()}</p>
      </div>
    </div>
  );
};

// Validação das propriedades recebidas pelo componente
RequestItem.propTypes = {
  request: PropTypes.shape({
    cliente: PropTypes.shape({
      id: PropTypes.number.isRequired,
      nome: PropTypes.string.isRequired,
    }).isRequired,
    musicas: PropTypes.arrayOf(
      PropTypes.shape({
        nome: PropTypes.string.isRequired,
        imagem: PropTypes.string,
        duracao: PropTypes.number.isRequired,
      })
    ).isRequired,
    data_requisicao: PropTypes.string.isRequired,
  }).isRequired,
};

export default RequestItem;
